import React, { useState } from 'react';
import axios from 'axios';

const CreateAG: React.FC = () => {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [agenda, setAgenda] = useState('');
  const [participants, setParticipants] = useState('');
  const [documents, setDocuments] = useState<File[]>([]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setDocuments(Array.from(event.target.files));
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('date', date);
    formData.append('time', time);
    formData.append('agenda', agenda);
    formData.append('participants', participants);

    documents.forEach((document, index) => {
      formData.append(`document_${index}`, document);
    });

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/ags', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Assemblée Générale créée avec succès:', response.data);
    } catch (error) {
      console.error('Erreur lors de la création de l\'Assemblée Générale:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Titre de l'AG:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Heure:
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Ordre du jour:
          <input 
            type={"text"}
            value={agenda}
            onChange={(e) => setAgenda(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
         Email des participants
          <input
            type={"text"}
            value={participants}
            onChange={(e) => setParticipants(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
         Lien de la réunion
          <input
            type={"text"}
            value={participants}
            onChange={(e) => setParticipants(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Documents pertinents:
          <input
            type="file"
            multiple
            onChange={handleFileChange}
          />
        </label>
      </div>
      <button type="submit">Créer l'AG</button>
    </form>
  );
};

export default CreateAG;
