import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../auth/AuthContext';
import DocumentUploader from './DocumentUploader';

interface Document {
  id: number;
  title: string;
  description: string;
  category: string;
  fileUrl: string;
  createdAt: Date;
  updatedAt: Date;
}

const Vault: React.FC = () => {
  const auth = useAuth();
  const [documents, setDocuments] = useState<Document[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [years, setYears] = useState<number[]>([]);
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedYear, setSelectedYear] = useState<number | undefined>(undefined);
  const [showUploader, setShowUploader] = useState(false);

  const fetchCategories = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/documents/categories', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      setCategories(response.data);
    } catch (error) {
      alert('Error fetching categories');
    }
  };

  const fetchYears = async (category: string) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/documents/years', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true,
        params: { category }
      });
      setYears(response.data);
    } catch (error) {
      alert('Error fetching years');
    }
  };

  app.get('/documents/years', async (req: Request, res: Response) => {
    const { category } = req.query;

    if (!category) {
      return res.status(400).send({ error: 'Category is required' });
    }

    try {
      const documentRepository = AppDataSource.getRepository(Document);
      const years = await documentRepository
        .createQueryBuilder('document')
        .select('YEAR(document.createdAt)', 'year')
        .where('document.category = :category', { category })
        .distinct(true)
        .getRawMany();
      res.status(200).json(years.map(y => y.year));
    } catch (error) {
      console.log(error);
      res.status(500).send({ error: 'Internal error, retry later' });
    }
  });


  const fetchDocuments = async (category: string, year?: number) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/documents', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true,
        params: {
          category,
          year
        }
      });
      setDocuments(response.data.document);
    } catch (error) {
      alert('Error fetching documents');
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const handleCategoryChange = (category: string) => {
    setSelectedCategory(category);
    setSelectedYear(undefined);
    setDocuments([]);
    fetchYears(category);
  };

  const handleYearChange = (year: number) => {
    setSelectedYear(year);
    fetchDocuments(selectedCategory, year);
  };

  const handleToggleUploader = () => {
    setShowUploader(!showUploader);
  };

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Coffre-fort numérique</h2>
      {auth.user.role === 'admin' && (
        <div>
          <p>Accès complet pour l'Admin.</p>
          <button onClick={handleToggleUploader}>
            {showUploader ? 'Masquer' : 'Ajouter un fichier'}
          </button>
          {showUploader && <DocumentUploader onUploadSuccess={() => fetchDocuments(selectedCategory, selectedYear)} />}
        </div>
      )}

      <div>
        <h3>Filtrer les documents</h3>
        <div>
          <label>
            Catégorie:
            <select value={selectedCategory} onChange={(e) => handleCategoryChange(e.target.value)}>
              <option value="">Sélectionner une catégorie</option>
              {categories.map((category) => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </label>
        </div>
        {selectedCategory && (
          <div>
            <label>
              Année:
              <select value={selectedYear} onChange={(e) => handleYearChange(Number(e.target.value))}>
                <option value="">Sélectionner une année</option>
                {years.map((year) => (
                  <option key={year} value={year}>
                    {year}
                  </option>
                ))}
              </select>
            </label>
          </div>
        )}
      </div>

      <div>
        <h3>Documents</h3>
        {documents.length > 0 ? (
          <ul>
            {documents.map((doc) => (
              <li key={doc.id}>
                <h4>{doc.title}</h4>
                <p>{doc.description}</p>
                <p>Catégorie: {doc.category}</p>
                <a href={`http://localhost:3000/uploads/${doc.fileUrl.split('/').pop()}`} target="_blank" rel="noopener noreferrer">
                  Télécharger le fichier
                </a>
                <p>Créé le: {new Date(doc.createdAt).toLocaleDateString()}</p>
                <p>Mis à jour le: {new Date(doc.updatedAt).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucun document disponible.</p>
        )}
      </div>
    </div>
  );
};

export default Vault;
