import React from 'react'; 
import event1 from '../../img/timeline/event1.jpg';
import event2 from '../../img/timeline/event2.png';
import event3 from '../../img/timeline/event3.jpg';
import event4 from '../../img/timeline/event4.jpg';
import event5 from '../../img/timeline/event5.jpg';
import '../../styles/home.css';

const Timeline: React.FC = () => {
    const scrollTimeline = (direction: 'left' | 'right') => {
        const container = document.querySelector('.timeline-container') as HTMLElement;
        if (container) {
            const scrollAmount = direction === 'left' ? -300 : 300;
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
        }
    };

    return (
        <section className="timeline-section">
            <h2>L’UFC-Que Choisir en quelques dates</h2>
            <div className="timeline-wrapper">
                <div className="timeline-container">
                    <div className="timeline-item">
                        <img src={event1} alt="1951" />
                        <p><strong>1951</strong><br />Création de l'Union Fédérale de la Consommation (UFC)</p>
                    </div>
                    <div className="timeline-item">
                        <img src={event2} alt="1961" />
                        <p><strong>1961</strong><br />Parution du premier Que Choisir mensuel</p>
                    </div>
                    <div className="timeline-item">
                        <img src={event3} alt="1962" />
                        <p><strong>1962</strong><br />Participation à la fondation du BEUC</p>
                    </div>
                    <div className="timeline-item">
                        <img src={event4} alt="1965" />
                        <p><strong>1965</strong><br />Création de la première association qui s’affilie à l’UFC</p>
                    </div>
                    <div className="timeline-item">
                        <img src={event5} alt="1975" />
                        <p><strong>1975</strong><br />Pollution et qualité</p>
                    </div>
                    <div className="timeline-item">
                        <img src={event5} alt="1975" />
                        <p><strong>1975</strong><br />Pollution et qualité</p>
                    </div>

                </div>
                <button className="scroll-button left" onClick={() => scrollTimeline('left')}>{'<'}</button>
                <button className="scroll-button right" onClick={() => scrollTimeline('right')}>{'>'}</button>
            </div>
        </section>
    );
}

export default Timeline;
