import React, { useState } from 'react';
import axios from 'axios';

interface DocumentUploaderProps {
  onUploadSuccess: () => void;
}

const DocumentUploader: React.FC<DocumentUploaderProps> = ({ onUploadSuccess }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');  // État pour la catégorie
  const [file, setFile] = useState<File | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!file) {
      alert('Veuillez télécharger un fichier.');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('category', category);  // Ajouter la catégorie aux données du formulaire
    formData.append('file', file);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/documents', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        },
      });
      setTitle('');
      setDescription('');
      setCategory('');  // Réinitialiser la catégorie
      setFile(null);
      (event.target as HTMLFormElement).reset();

      onUploadSuccess();

    } catch (error) {
      console.error('Error uploading document:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Titre:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Description:
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Catégorie:
          <input
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Télécharger un fichier:
          <input
            type="file"
            onChange={handleFileChange}
            required
          />
        </label>
      </div>
      <button type="submit">Soumettre</button>
    </form>
  );
};

export default DocumentUploader;
