import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import '../../styles/home.css';

const stripePromise = loadStripe('your-public-key-here');

const DonationForm: React.FC = () => {
    const stripe = useStripe();
    const elements = useElements();
    const [amount, setAmount] = useState<number | null>(null);
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [streetNumber, setStreetNumber] = useState('');
    const [streetName, setStreetName] = useState('');
    const [postalCode, setPostalCode] = useState('');
    const [city, setCity] = useState('');
    const [errors, setErrors] = useState<string[]>([]);

    const handleSubmit = async (event: React.FormEvent) => {
        event.preventDefault();

        const newErrors: string[] = [];

        if (!/^[A-Za-zÀ-ÿ\s'-]+$/.test(name)) newErrors.push('Le nom ne doit contenir que des lettres, des espaces, des tirets ou des apostrophes.');
        if (!/^[A-Za-zÀ-ÿ\s'-]+$/.test(surname)) newErrors.push('Le prénom ne doit contenir que des lettres, des espaces, des tirets ou des apostrophes.');
        if (!/^[A-Za-zÀ-ÿ\s'-]+$/.test(city)) newErrors.push('La ville ne doit contenir que des lettres, des espaces, des tirets ou des apostrophes.');
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) newErrors.push('L\'email n\'est pas valide.');
        if (!/^\d{5}$/.test(postalCode)) newErrors.push('Le code postal doit contenir exactement 5 chiffres.');

        setErrors(newErrors);

        if (newErrors.length > 0) return;

        if (!stripe || !elements) {
            return;
        }

        const cardElement = elements.getElement(CardElement);

        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: cardElement!,
        });

        if (!error && paymentMethod) {
            const response = await fetch('http://localhost:3000/create-payment-intent', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    amount: amount,
                    paymentMethodId: paymentMethod.id,
                }),
            });

            const paymentResult = await response.json();
            if (paymentResult.error) {
                console.error(paymentResult.error);
            } else {
                console.log('Payment successful');
            }
        } else {
            setErrors([error?.message || 'Erreur lors de la création du moyen de paiement']);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="donation-form">
            <div className="amount-section">
                <div className="amount-options">
                    <button type="button" onClick={() => setAmount(5)}>5€</button>
                    <button type="button" onClick={() => setAmount(30)}>30€</button>
                    <button type="button" onClick={() => setAmount(50)}>50€</button>
                    <button type="button" onClick={() => setAmount(100)}>100€</button>
                    <input
                        type="number"
                        placeholder="Autre montant"
                        className='otherAmount'
                        onChange={(e) => setAmount(Number(e.target.value))}
                    />
                </div>
            </div>
            <div className="info-section">
                <label>
                    Nom
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </label>
                <label>
                    Prénom
                    <input type="text" value={surname} onChange={(e) => setSurname(e.target.value)} required />
                </label>
                <label>
                    Email
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </label>
                <label>
                    Numéro de rue
                    <input type="text" value={streetNumber} onChange={(e) => setStreetNumber(e.target.value)} required />
                </label>
                <label>
                    Nom de la voie
                    <input type="text" value={streetName} onChange={(e) => setStreetName(e.target.value)} required />
                </label>
                <label>
                    Code Postal
                    <input type="text" value={postalCode} onChange={(e) => setPostalCode(e.target.value)} required />
                </label>
                <label>
                    Ville
                    <input type="text" value={city} onChange={(e) => setCity(e.target.value)} required />
                </label>
            </div>
            <div className="payment-section">
                <CardElement />
            </div>
            <button type="submit" disabled={!stripe || !amount}>
                Faire un don {amount ? `de ${amount}€` : ''}
            </button>
            {errors.length > 0 && (
                <div className="error-messages">
                    {errors.map((error, index) => (
                        <p key={index} className="error">{error}</p>
                    ))}
                </div>
            )}
        </form>
    );
};

const Donation: React.FC = () => {
    return (
        <Elements stripe={stripePromise}>
            <div className="donation-container">
                <h1>Faire un don</h1>
                <DonationForm />
            </div>
        </Elements>
    );
};

export default Donation;
