import React, { useState } from 'react';

interface Document {
  id: number;
  title: string;
  description: string;
  category: string;
  fileUrl: string;
  createdAt: Date;
  updatedAt: Date;
}

interface DocumentViewerProps {
  document: Document;
}

const DocumentViewer: React.FC<DocumentViewerProps> = ({ document }) => {
  const [isPreviewVisible, setPreviewVisible] = useState(false);

  const fileExtension = document.fileUrl.split('.').pop()?.toLowerCase();
  const fileUrl = `http://localhost:3000/${document.fileUrl}`;

  const renderDocument = () => {
    if (!fileExtension) {
      return null;
    }

    switch (fileExtension) {
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return <img src={fileUrl} alt={document.title} style={{ maxWidth: '100%' }} />;
      case 'pdf':
        return (
          <object data={fileUrl} type="application/pdf" width="100%" height="500px">
            <p>Votre navigateur ne supporte pas la pré-visualisation des PDFs. <a href={fileUrl} target="_blank" rel="noopener noreferrer">Télécharger PDF</a></p>
          </object>
        );
      case 'txt':
        return (
          <iframe src={fileUrl} title={document.title} width="100%" height="500px" />
        );
      case 'mp4':
      case 'webm':
        return (
          <video controls width="100%">
            <source src={fileUrl} type={`video/${fileExtension}`} />
            Votre navigateur ne supporte pas la pré-visualisation des vidéos.
          </video>
        );
      default:
        return <a href={fileUrl} target="_blank" rel="noopener noreferrer">Télécharger le fichier</a>;
    }
  };

  return (
    <div>
      <h3>{document.title}</h3>
      <p>{document.description}</p>
      {['jpg', 'jpeg', 'png', 'gif', 'pdf'].includes(fileExtension ?? '') && (
        <button onClick={() => setPreviewVisible(!isPreviewVisible)}>
          {isPreviewVisible ? 'Masquer la prévisualisation' : 'Prévisualiser'}
        </button>
      )}
      {isPreviewVisible && renderDocument()}
      <a href={fileUrl} target="_blank" rel="noopener noreferrer" download>
        Télécharger le fichier
      </a>
    </div>
  );
};

export default DocumentViewer;
