import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../auth/AuthContext';
import DocumentUploader from './DocumentUploader';

interface Document {
  id: number;
  title: string;
  description: string;
  fileUrl: string;
  createdAt: Date;
  updatedAt: Date;
}

const Vault: React.FC = () => {
  const auth = useAuth();
  const [documentCount, setDocumentCount] = useState<number>(0);
  const [showUploader, setShowUploader] = useState(false);

  const fetchDocumentCount = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.get('http://localhost:3000/documents/count', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      setDocumentCount(response.data.count);  
    } catch (error) {
      alert('Error fetching document count');
    }
  };

  useEffect(() => {
    fetchDocumentCount();
  }, []);

  const handleToggleUploader = () => {
    setShowUploader(!showUploader);
  };

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Coffre-fort numérique</h2>
      {auth.user.role === 'admin' && (
        <div>
          <p>Accès complet pour l'Admin.</p>
          <button onClick={handleToggleUploader}>
            {showUploader ? 'Masquer' : 'Ajouter un fichier'}
          </button>
          {showUploader && <DocumentUploader onUploadSuccess={fetchDocumentCount} />}
        </div>
      )}

      <div>
        <h3>Documents</h3>
        <p>Nombre de documents disponibles: {documentCount}</p>
      </div>
    </div>
  );
};

export default Vault;
