import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import {  CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import '../../styles/home.css';

export default function Donation (){

    const stripe = useStripe();
    const elements = useElements();
    const [amount, setAmount] = useState<number | null>(null);

    const handleSubmit = async (event: React.FormEvent) => {
        event.preventDefault();

        if (!stripe || !elements) {
            return;
        }

        const cardElement = elements.getElement(CardElement);

        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: cardElement!,
        });

        if (!error && paymentMethod) {
            const response = await fetch('http://localhost:3000/create-payment-intent', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    amount: amount,
                    paymentMethodId: paymentMethod.id,
                }),
            });

            const paymentResult = await response.json();
            if (paymentResult.error) {
                console.error(paymentResult.error);
            } else {
                console.log('Payment successful');
            }
        } else {
            console.error(error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="donation-form">
            <div className="amount-section">
                <label>Montant du don</label>
                <div className="amount-options">
                    <button type="button" onClick={() => setAmount(5)}>5â‚¬</button>
                    <button type="button" onClick={() => setAmount(30)}>30â‚¬</button>
                    <button type="button" onClick={() => setAmount(50)}>50â‚¬</button>
                    <button type="button" onClick={() => setAmount(100)}>100â‚¬</button>
                    <input
                        type="number"
                        placeholder="Autre montant"
                        onChange={(e) => setAmount(Number(e.target.value))}
                    />
                </div>
            </div>
            <div className="info-section">
                <label>
                    Nom **
                    <input type="text" required />
                </label>
                <label>
                    PrÃ©nom **
                    <input type="text" required />
                </label>
                <label>
                    Courriel **
                    <input type="email" required />
                </label>
                <label>
                    Confirmation courriel **
                    <input type="email" required />
                </label>
                <label>
                    NumÃ©ro de rue **
                    <input type="text" required />
                </label>
                <label>
                    Nom de la voie **
                    <input type="text" required />
                </label>
                <label>
                    Code Postal **
                    <input type="text" required />
                </label>
                <label>
                    Ville **
                    <input type="text" required />
                </label>
            </div>
            <div className="payment-section">
                <CardElement />
            </div>
            <button type="submit" disabled={!stripe || !amount}>
                Faire un don de {amount ? `${amount}â‚¬` : ''}
            </button>
        </form>
    );
};
