import React from 'react';

interface VoteQuestion {
  questionText: string;
  options: { id: number, text: string }[];
}

interface Vote {
  id: number;
  title: string;
  questions: VoteQuestion[];
  mode: string;
  comment?: string;
}

interface VoteResultsProps {
  vote: Vote;
  responses: string[][];
}

const VoteResults: React.FC<VoteResultsProps> = ({ vote, responses }) => {
  const countResponses = (questionIndex: number, optionText: string) => {
    return responses.filter(response => response[questionIndex] === optionText).length;
  };

  return (
    <div>
      <h2>RÃ©sultats pour: {vote.title}</h2>
      {vote.questions.map((question, qIndex) => (
        <div key={qIndex}>
          <h4>{question.questionText}</h4>
          <ul>
            {question.options.map((option, oIndex) => (
              <li key={oIndex}>
                {option.text}: {countResponses(qIndex, option.text)}
              </li>
            ))}
          </ul>
        <br/>
        </div>
      ))}
    </div>
  );
};

export default VoteResults;
