import React, { useState } from 'react';
import axios from 'axios';

const CreateAG: React.FC = () => {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [agenda, setAgenda] = useState('');
  const [email, setEmail] = useState('');
  const [link, setLink] = useState('');
  const [documents, setDocuments] = useState<File[]>([]);
  const [error, setError] = useState<string | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setDocuments(Array.from(event.target.files));
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError(null);

    const currentDate = new Date();
    const agDate = new Date(`${date}T${time}`);

    if (agDate < currentDate) {
      setError('La date et l\'heure de l\'AG ne peuvent pas être inférieures à la date actuelle.');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('date', date);
    formData.append('time', time);
    formData.append('agenda', agenda);
    formData.append('email', email);
    formData.append('link', link);

    documents.forEach((document, index) => {
      formData.append(`document_${index}`, document);
    });

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/ags', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
    } catch (error) {
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <label>
          Titre de l'AG:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Heure:
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Ordre du jour:
          <input 
            type={"text"}
            value={agenda}
            onChange={(e) => setAgenda(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
         Email des participants
          <input
            type={"text"}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
         Lien de la réunion
          <input
            type={"text"} 
            value={link}
            onChange={(e) => setLink(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Documents pertinents:
          <input
            type="file"
            multiple
            onChange={handleFileChange}
          />
        </label>
      </div>
      <button type="submit">Créer l'AG</button>
    </form>
  );
};

export default CreateAG;
