import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../auth/AuthContext';
import DocumentUploader from './DocumentUploader';

interface Document {
  id: number;
  title: string;
  description: string;
  category: string;  // Nouvelle propriété pour la catégorie
  fileUrl: string;
  createdAt: Date;
  updatedAt: Date;
}

const Vault: React.FC = () => {
  const auth = useAuth();
  const [documents, setDocuments] = useState<Document[]>([]);
  const [showUploader, setShowUploader] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedYear, setSelectedYear] = useState<number | undefined>(undefined);

  const fetchDocuments = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.get('http://localhost:3000/documents', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true,
        params: {
          category: selectedCategory,
          year: selectedYear
        }
      });
      setDocuments(response.data.document);  
    } catch (error) {
      alert('Error fetching documents');
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, [selectedCategory, selectedYear]);

  const handleToggleUploader = () => {
    setShowUploader(!showUploader);
  };

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Coffre-fort numérique</h2>
      {auth.user.role === 'admin' && (
        <div>
          <p>Accès complet pour l'Admin.</p>
          <button onClick={handleToggleUploader}>
            {showUploader ? 'Masquer' : 'Ajouter un fichier'}
          </button>
          {showUploader && <DocumentUploader onUploadSuccess={fetchDocuments} />}
        </div>
      )}

      <div>
        <h3>Filtrer les documents</h3>
        <div>
          <label>
            Catégorie:
            <input
              type="text"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            />
          </label>
        </div>
        <div>
          <label>
            Année:
            <input
              type="number"
              value={selectedYear || ''}
              onChange={(e) => setSelectedYear(Number(e.target.value))}
            />
          </label>
        </div>
        <button onClick={fetchDocuments}>Filtrer</button>
      </div>

      <div>
        <h3>Documents</h3>
        {documents.length > 0 ? (
          <ul>
            {documents.map((doc) => (
              <li key={doc.id}>
                <h4>{doc.title}</h4>
                <p>{doc.description}</p>
                <p>Catégorie: {doc.category}</p>
                <a href={`http://localhost:3000/uploads/${doc.fileUrl.split('/').pop()}`} target="_blank" rel="noopener noreferrer">
                  Télécharger le fichier
                </a>
                <p>Créé le: {new Date(doc.createdAt).toLocaleDateString()}</p>
                <p>Mis à jour le: {new Date(doc.updatedAt).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucun document disponible.</p>
        )}
      </div>
    </div>
  );
};

export default Vault;
