import React, { useState } from 'react';
import axios from 'axios';

const Login: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/auth/login', {
        username,
        password
      });

      localStorage.setItem('token', response.data.token);
      window.location.href = '/app';
    } catch (error) {
      setError('Invalid credentials');
    }
  };

  const signUpPage = async (event: React.FormEvent) => {
    window.location.href = '/signup';
  };

  return (
    <div>
      <div className='header'>
        <img src={`${process.env.PUBLIC_URL}/favicon.svg.png`} alt="Favicon" className='favicon'/>
        <h2>UFC Que Choisir</h2>
      </div>

      <form onSubmit={handleSubmit}>

          <label>Nom d'utilisateur </label>
          <br/>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          /> 
          <br/>
          <br/>

          <label>Mot de passe </label>
          <br/>

          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        <br/>

        {error && <p className='error'>{error}</p>}
        <button type="submit" onClick={signUpPage}>Se connecter</button>
        <br/>
        <br/>
        <hr/>
        <a href='' className='forgottenPassword'>Mot de passe oubli√©</a>
        <br/>
        <br/>
      </form>
    </div>
  );
};

export default Login;
